// vh|vw|vmin|vmax
;(function() {
  var needle = document.createElement('div')
  needle.style.width = '50vw'
  document.body.appendChild(needle)
  if (
    parseInt(window.innerWidth / 2, 10) !==
    parseInt(getComputedStyle(needle, null).width, 10)
  ) {
    require('viewport-units-buggyfill').init({
      hacks: require('viewport-units-buggyfill/viewport-units-buggyfill.hacks')
    })
  }
  needle.parentNode.removeChild(needle)
  needle = null
})()
