'use strict'
const cmd = require('caporal')
const chalk = require('chalk')
const logo = require('./assets/logo')
const { version } = require('./package.json')
const args = process.argv.slice(2)

!args.length && console.log(logo.replace('{version}', version))

require('./modules/')(cmd)
  .name('fe')
  .version(version)
  .description(
    `Front-End workflow & stack\n   🍺  ${chalk.magenta(
      'fe init'
    )} ${chalk.gray('»')} ${chalk.magenta('fe dev')} ${chalk.gray(
      '»'
    )} ${chalk.magenta('fe start')}`
  )
  .parse(process.argv)
