const { inline } = require('../../utils/console')
module.exports = async (args, options, logger) => {
  // Reset NODE_ENV as production
  process.env.NODE_ENV = 'production'
  const runtime = await require('../runtime/')()
  const { CLIENT_MODE } = runtime.project.config
  CLIENT_MODE &&
    (await require(`./plugins/${CLIENT_MODE}`)(runtime, options).catch(err => {
      inline.error(err)
      process.exit(0)
    }))
}
